mapper.delete:
delete from jy_delivery_customer where delivery_id
in

	#{deCustomerId}

mapper.sql:
and jc.customer_name like "%${param3}%"
and ja.aging_address like "%${param4}%"
ORDER BY ja.aging_address ASC limit #{param1},#{param2}

mapper.select:
SELECT COUNT(ja.aging_id)
from jy_aging ja WHERE
ja.aging_address=#{aging_address} AND
ja.aging_cutomer_id=#{aging_cutomer_id}
AND ja.star_time=#{star_time}
AND ja.end_time=#{end_time}

mapper.insert:
insert into jy_delivery_customer( delivery_id, delivery_num, delivery_name, delivery_people, delivery_address, delivery_tel, delivery_business, delivery_profile, delivery_experience, delivery_cus_name
) values
( #{item.delivery_id}, #{item.delivery_num}, #{item.delivery_name}, #{item.delivery_people}, #{item.delivery_address}, #{item.delivery_tel}, #{item.delivery_business}, #{item.delivery_profile}, #{item.delivery_experience}, #{item.delivery_cus_name} )
